import React from 'react';

import Movie from './Movie';
import classes from './MoviesList.module.css';

const MovieList = (props) => {

  const DeleteHandler= async (id)=>{
    try{
      const response= await fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/movies/${id}.json`,{
      method: 'DELETE',
      })
      if(!response.ok){
        throw new Error('Something went Wrong')
      }
    } catch(error){
       console.log(error)
    }

    const ID=props.movies.map((movie)=>movie.id)
    const index=ID.findIndex(id)
    props.movies.splice(index,1)
  }




  return (
    <ul className={classes['movies-list']}>
      {props.movies.map((movie) => (
        <Movie
          key={movie.id}
          title={movie.title}
          releaseDate={movie.releaseDate}
          openingText={movie.openingText}
          onRemove={DeleteHandler.bind(null,movie.id)}
        />

      ))}

    </ul>
  );
};

export default MovieList;
