import React,{useCallback, useEffect, useState} from "react";
import { useSelector } from "react-redux";
import TaskDetails from "../AddTasks/TaskDetails";

const Completedtasks=()=>{
    const [completed,setCompleted]=useState([])
    const userId=useSelector((state)=>state.Auth.UID)

    const fetchCompletedTasks= useCallback( async ()=>{
        try{
            const response =await fetch(`https://to-do-list-3e614-default-rtdb.asia-southeast1.firebasedatabase.app/Completed/${userId}.json`)
            if(!response.ok){
                throw new Error('Could not fetch completed tasks')
            }
            const data= await response.json()
            const loadedTasks=[]
            for(const key in data){
                loadedTasks.push({
                    id: key,
                    task: data[key].tasks,
                    startDate: data[key].startDate,
                    endDate: data[key].endDate,
                    description: data[key].description
                })
            }
            setCompleted(loadedTasks)
        }catch(err){
            alert(err.message)
        }
    },[completed,userId])

    useEffect(()=>{
        fetchCompletedTasks()
    },[fetchCompletedTasks])

    const DeleteTaskHandler=async (id)=>{
        try{
            const response =await fetch(`https://to-do-list-3e614-default-rtdb.asia-southeast1.firebasedatabase.app/Tasks/${userId}/${id}.json`,{
              method: 'DELETE'
            })
            if(response.ok){
              alert('Updated')
            }else{
              throw new Error('Something Went Wrong')
            }
          }catch(err){
            alert(err.message)
          }
          const index=completed.findIndex((i)=>i.id===id)
          completed.splice(index,1)
          setCompleted(completed)
    }

    return(
        <React.Fragment>
            <h1>Completed Tasks</h1>
            {completed.map((entTask) => (
          <TaskDetails
            key={entTask.id}
            id={entTask.id}
            startDate={entTask.startDate}
            endDate={entTask.endDate}
            description={entTask.description}
            task={entTask.tasks}
            completedTask={true}
            onDelete={DeleteTaskHandler.bind(null, entTask.id)}
          />))}
        </React.Fragment>
    )
}

export default Completedtasks
