import React, { useState } from "react";
import AddTaskForm from "./AddTaskForm";
import { useDispatch, useSelector } from "react-redux";
import "./AddTasks.css"; // Import your custom CSS file

const AddTasks = () => {
  const [addTask, setAddTask] = useState(false);
  const dispatch= useDispatch()
  const [enteredtasks,setTasks]=useState([])
  const userId=useSelector((state)=>state.Auth.UID)

  useEffect(()=>{
    fetchtask()
  },[])

  const fetchtask= async ()=>{

    try{
        const response =await fetch(`https://to-do-list-3e614-default-rtdb.asia-southeast1.firebasedatabase.app/Tasks/${userId}.json`)
        if (response.ok) {
            const data = await response.json();
            
            const loadedTasks = [];
            for (const key in data) {
                loadedTasks.push({
                  id: key,
                  tasks: data[key].tasks,
                  startDate: data[key].startDate,
                  endDate: data[key].endDate
                });
             
            }
            setTasks(loadedTasks);
          } else {
            throw new Error('Failed to fetch Tasks');
          }
    }catch(err){
        alert(err.message)
    }
  }

  const SubmitNewTask= async (enteredTask,startDate,endDate)=>{
    try{
        const response =await fetch(`https://to-do-list-3e614-default-rtdb.asia-southeast1.firebasedatabase.app/Tasks/${userId}.json`,{
            method: 'POST',
            body: JSON.stringify({
                tasks: enteredTask,
                startDate: startDate,
                endDate: endDate
            })
        })
        if (response.ok) {
            alert('Task has been updated');
            
            fetchtask();
          } else {
            throw new Error('Could not store the Task');
          }
    }catch(err){
        alert(err.message)
    }
  }

  const addTaskHandler = () => {
    setAddTask(true);
  };

  const StateChangeHandler=(f)=>{
    setAddTask(f)
  }

  return (
    <div className="add-tasks-container">
      {!addTask && (
        <button className="add-task-button" onClick={addTaskHandler}>
          Add New Task
        </button>
      )}
      {addTask && <AddTaskForm onStateChange={StateChangeHandler} onSubmitTask={()=>{SubmitNewTask(task,startDate,endDate)}} />}
    </div>
  );
};

export default AddTasks;
