import React,{useState, useEffect, useCallback} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {

  const [fetchMovies,setFetchMovies]=useState([])
  const [IsLoading,setisLoading]=useState(false)
  const [error,setError]=useState(null)
  const [retry,setRetry]=useState(false)


  const fetchMovieshandler=useCallback( async () =>{
    setError(null)
    setisLoading(true)
    try{
      const response= await fetch('https://swapi.dev/api/film/')


    if(!response.ok){
      throw new Error('Something went wrong...Retrying')
    }

    const data= await response.json()

    const TransformerdData=data.results.map(MovieData=>{
        return { 
            id: MovieData.episode_id,
            title: MovieData.title,
            openingText: MovieData.opening_crawl,
            releaseDate: MovieData.release_date
          }
       }
      )
      setFetchMovies(TransformerdData)
    }
    catch (error) {
      setError(error.message)
    }
    setisLoading(false)
  },[])

  useEffect(()=>{
    const identifier= setTimeout(()=>{fetchMovieshandler()},5000)
    return ()=>{clearTimeout(identifier)}
  },[error])


  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieshandler}>Fetch Movies</button>
      </section>
      <section>
        {IsLoading && <p>Loading...</p>}
        {!IsLoading && fetchMovies.length===0 && !error && <p>No Movies Found.</p>}
        {!IsLoading && fetchMovies.length>0 && <MoviesList movies={fetchMovies} />}
        {!IsLoading && error && <p>
          {error} 
          <button onClick={retryhandler}>Cancel</button> 
        </p>}
      </section>
    </React.Fragment>
  );
}

export default App;
