import React,{useState} from "react";
import classes from "./MovieForm.module.css"

const MovieForm=()=>{

    const [Title,setTitle]=useState('')
    const [OpText,setOpText]=useState('')
    const [RD,setRD]=useState('')

    const TitleChangeHandler=(event)=>{
        setTitle(event.target.value)
    }

    const TextChangeHandler=(event)=>{
        setOpText(event.target.value)
    }

    const DateChangeHandler=(event)=>{
        setRD(event.target.value)
    }

    const Submithandler=(event)=>{
        event.preventDefault()
        const obj={
            title: Title,
            openingText: OpText,
            releaseDate: RD
        }
        console.log(obj)
        setTitle('')
        setRD('')
        setOpText('')
    }


    return(
        <form className={classes.form} onSubmit={Submithandler}>
            <div className={classes.control}>
                <label htmlFor="title">Title</label>
                <input id="title" value={Title} onChange={TitleChangeHandler} />
            </div>
            <div>
                <label htmlFor="text">Opening Text</label>
                <input id="text" value={OpText} onChange={TextChangeHandler} />
            </div>
            <div>
                <label htmlFor="date">Release Date</label>
                <input id="date" value={RD} onChange={DateChangeHandler} />
            </div>
            <button type="submit" className={classes.button}>Add Movie</button>
        </form>
    )
}

export default MovieForm
