import React, { useState } from "react";
import AddTaskForm from "./AddTaskForm";
import "./AddTasks.css"; // Import your custom CSS file

const AddTasks = () => {
  const [addTask, setAddTask] = useState(false);

  const addTaskHandler = () => {
    setAddTask(true);
  };

  const StateChangeHandler=(f)=>{
    setAddTask(f)
  }

  return (
    <div className="add-tasks-container">
      {!addTask && (
        <button className="add-task-button" onClick={addTaskHandler}>
          Add New Task
        </button>
      )}
      {addTask && <AddTaskForm onStateChange={StateChangeHandler} />}
    </div>
  );
};

export default AddTasks;
